#include "HTSuperproSensors.h"
task main()
{

	SuperSensors_init_task_yaw(S2);
	while(!super_health)
		sleep(20);
	while(true)
	{
		float yaw=SuperSensors_getHeading();
		displayTextLine(1, "y: %f", yaw);
		writeDebugStreamLine("yaw in main:%d",yaw);
		sleep(20);
	}
}
